完整代码 <pre><code>#include &lt;bits/stdc++.h&gt;
#define int long long
using namespace std;
int n, m, a[1000006];
constexpr int mod = 998244353;
signed main()
{
    ios::sync_with_stdio(false), cin.tie(0);
    cin &gt;&gt; n &gt;&gt; m;
    while (m--)
    {
        int l, r;
        cin &gt;&gt; l &gt;&gt; r;
        int len = r - l + 1;
        a[l] = (a[l] + 1) % mod;
        a[l + 1] = (a[l + 1] + 1) % mod;
        a[r + 1] = (a[r + 1] + (-(len * len) - 2 * len - 1)) % mod;
        a[r + 2] = (a[r + 2] + 2 * len * len + 2 * len - 1) % mod;
        a[r + 3] = (a[r + 3] - (len * len)) % mod;
    }
    for (int i = 1; i &lt;= n; i++)
        a[i] = (a[i] + a[i - 1]) % mod;
    for (int i = 1; i &lt;= n; i++)
        a[i] = (a[i] + a[i - 1]) % mod;
    for (int i = 1; i &lt;= n; i++)
        a[i] = (a[i] + a[i - 1]) % mod;
    for (int i = 1; i &lt;= n; i++)
        cout &lt;&lt; (a[i] + mod) % mod &lt;&lt; " ";
    return 0;
}
