#define int long long
using namespace std;
constexpr int N = 1e6 + 5;
int n, m, a[N], b[N], c[N];
bool check(int k)
{
    for (int i = 1; i &lt;= n; i++)
        c[i] = 0;
    for (int i = 1; i &lt;= m; i++)
    {
        if (b[i] - k &gt;= 0)
        {
            c[b[i] - k + 1] += 1;
            c[b[i] + 1] += -2;
        }
        else
        {
            c[1] += k - (b[i] - 1);
            c[2] += 1 - (k - (b[i] - 1));
            c[b[i] + 1] += -2;
        }
        if (b[i] + k &lt;= n + 1)
        {
            c[b[i] + k + 1] += 1;
        }
    }
    for (int i = 1; i &lt;= n; i++)
        c[i] += c[i - 1];
    for (int i = 1; i &lt;= n; i++)
    {
        c[i] += c[i - 1];
        if (c[i] &lt; a[i]) return false;
    }
    return true;
}
signed main()
{
    ios::sync_with_stdio(false), cin.tie(0);
    cin &gt;&gt; n &gt;&gt; m;
    for (int i = 1; i &lt;= n; i++)
        cin &gt;&gt; a[i];
    for (int i = 1; i &lt;= m; i++)
        cin &gt;&gt; b[i];
    int l = 0, r = 1e10, ans = 0;
    while (l &lt;= r)
    {
        int mid = (l + r) &gt;&gt; 1;
        if (check(mid))
        {
            ans = mid;
            r = mid - 1;
        }
        else
        {
            l = mid + 1;
        }
    }
    cout &lt;&lt; ans;
    return 0;
}
