#define int long long
using namespace std;
constexpr int mod = 998244353;
int t, id, n, m, c, f, r[1005][1005], d[1005][1005], a[1005][1005], sum[1005][1005], C, F;
signed main()
{
    ios::sync_with_stdio(false), cin.tie(0);
    cin &gt;&gt; t &gt;&gt; id;
    while (t--)
    {
        memset(r, 0, sizeof(r));
        memset(d, 0, sizeof(d));
        memset(a, 0, sizeof(a));
        memset(sum, 0, sizeof(sum));
        cin &gt;&gt; n &gt;&gt; m &gt;&gt; c &gt;&gt; f;
        C = 0, F = 0;
        for (int i = 1; i &lt;= n; i++)
        {
            for (int j = 1; j &lt;= m; j++)
            {
                char c;
                cin &gt;&gt; c;
                a[i][j] = c - '0';
            }
        }
        for (int i = 1; i &lt;= n; i++)
        {
            for (int j = m; j &gt;= 1; j--)
            {
                r[i][j] = (a[i][j] ? 0 : r[i][j + 1] + 1);
            }
        }
        for (int i = n; i &gt;= 1; i--)
        {
            for (int j = 1; j &lt;= m; j++)
            {
                d[i][j] = (a[i][j] ? 0 : d[i + 1][j] + 1);
            }
        }
        for (int x2 = 3; x2 &lt;= n; x2++)
        {
            for (int y0 = 1; y0 &lt;= m; y0++)
            {
                if (d[x2 - 2][y0] &gt;= 3)
                {
                    sum[x2][y0] = (sum[x2 - 1][y0] + r[x2 - 2][y0] - 1) % mod;
                }
            }
        }
        for (int x2 = 3; x2 &lt;= n; x2++)
        {
            for (int y0 = 1; y0 &lt;= m; y0++)
            {
                C = (C + sum[x2][y0] * (r[x2][y0] - 1)) % mod;
                F = (F + sum[x2][y0] * (r[x2][y0] - 1) % mod * (d[x2][y0] - 1)) % mod;
            }
        }
        cout &lt;&lt; c * C &lt;&lt; " " &lt;&lt; f * F &lt;&lt; "\n";
    }
    return 0;
}
