using namespace std;
const int N = 1e6 + 5;
int n, l[N], r[N], h = 0, t = 1e6 + 1, ans;
bool vis[N]; 
void ins(int x, int y) // x 右边插入 y
{
    int z = r[x];
    r[x] = y, l[y] = x;
    r[y] = z, l[z] = y;
    if (x != h &amp;&amp; (x + y) &amp; 1)
        ans++; 
} 
void del(int y)
{
    int x = l[y], z = r[y];
    r[x] = z, l[z] = x;
    if (x != h &amp;&amp; (x + y) &amp; 1) ans--; 
    if (z != t &amp;&amp; (y + z) &amp; 1) ans--;
    if (x != h &amp;&amp; z != t &amp;&amp; (x + z) &amp; 1) ans++; 
}
int main()
{
    cin &gt;&gt; n;
    r[h] = t, l[t] = h;
    while (n--)
    {
        int x;
        cin &gt;&gt; x;
        if (vis[x])
        {
            del(x);
        }
        vis[x] = 1;
        ins(l[t], x);
        cout &lt;&lt; ans &lt;&lt; "\n"; 
    } 
    return 0;
}
