完整代码 <pre><code>#include &lt;bits/stdc++.h&gt;
#define int long long
using namespace std;
const int N = 1e5 + 5;
int n, ans;
int cnt[N];
queue&lt;int&gt; q1, q2; 
int read()
{
    int s = 0, f = 1;
    char ch = getchar();
    while (ch &lt; '0' || ch &gt; '9')
    {
        if (ch == '-') f = -1;
        ch = getchar();
    }
    while (ch &gt;= '0' &amp;&amp; ch &lt;= '9')
    {
        s = s * 10 + ch - '0';
        ch = getchar();
    }
    return s * f;
}
int top()
{
    if (q2.empty() || !q1.empty() &amp;&amp; q1.front() &lt; q2.front())   
    {
        int x = q1.front();
        q1.pop();
        return x;
    }
    else
    {
        int x = q2.front();
        q2.pop();
        return x;
    }
}   
signed main()
{
    n = read();
    for (int i = 1; i &lt;= n; i++) 
    {
        int x = read();
        cnt[x]++;
    }
    for (int i = 1; i &lt;= 1e5; i++)
        while (cnt[i]--)
            q1.push(i);
    for (int i = 1; i &lt;= n - 1; i++)
    {
        int x = top();
        int y = top();
        ans += x + y;
        q2.push(x + y); // q2 放新合并的数字 
    }
    cout &lt;&lt; ans; 
    return 0;
}
