        f[u][v] = f[v][u] = min(f[u][v], w);
    }
    cin &gt;&gt; k &gt;&gt; t;
    for (int i = 1; i &lt;= k; i++)
    {
        int x;
        cin &gt;&gt; x;
        f[x][0] = t;
        f[0][x] = 0;
    }
    for (int k = 0; k &lt;= n; k++)
        for (int i = 0; i &lt;= n; i++)
            for (int j = 0; j &lt;= n; j++)
                f[i][j] = min(f[i][j], f[i][k] + f[k][j]);
    cin &gt;&gt; q;
    while (q--)
    {
        int op;
        cin &gt;&gt; op;
        if (op == 1)
        {
            int u, v, w;
