            f[u][v] = f[v][u] = min(f[u][v], w);
            for (int i = 0; i &lt;= n; i++)
            {
                for (int j = 0; j &lt;= n; j++)
                {
                    f[i][j] = min(f[i][j], f[i][u] + w + f[v][j]);
                    f[i][j] = min(f[i][j], f[i][v] + w + f[u][j]);
                }
            }
        }
        if (op == 2)
        {
            int x;
            cin &gt;&gt; x;
            f[x][0] = t;
            f[0][x] = 0;
            for (int i = 0; i &lt;= n; i++)
            {
                for (int j = 0; j &lt;= n; j++)
                {
                    f[i][j] = min(f[i][j], f[i][x] + t + f[0][j]);
                    f[i][j] = min(f[i][j], f[i][0] + 0 + f[x][j]);
