满分代码 <pre><code>#include &lt;bits/stdc++.h&gt;
using namespace std;
using ary = array&lt;int, 2&gt;;

void solve()
{
    int n, m, k;
    cin &gt;&gt; n &gt;&gt; m &gt;&gt; k;
    vector&lt;int&gt; a(n + 1);
    for (int i = 1; i &lt;= n; i++)
    {
        cin &gt;&gt; a[i];
        // 这里为了方便将汉堡设为 -1
        if (a[i] == 2)
            a[i] = -1;
    }
    vector&lt;vector&lt;ary&gt;&gt; e(n + 1);
    while (m--)
    {
        int u, v, w;
        cin &gt;&gt; u &gt;&gt; v &gt;&gt; w;
        e[u].push_back({v, w});
        e[v].push_back({u, w});
    }
    int s, t;
    cin &gt;&gt; s &gt;&gt; t;
    vector&lt;vector&lt;int&gt;&gt; dis(n + 1, vector&lt;int&gt;(25, 1e9)), vis(n + 1, vector&lt;int&gt;(25, 0));
    // dis[i][j] : 走到点 i 且可乐减去汉堡差值为 j 的最短路
    // 堆里存储三个信息，[距离，差值，编号]
    priority_queue&lt;array&lt;int, 3&gt;, vector&lt;array&lt;int, 3&gt;&gt;, greater&lt;array&lt;int, 3&gt;&gt;&gt; q;
    // 初始化起点
    dis[s][a[s] + 10] = 0;
    q.push({0, a[s] + 10, s});
    while (q.size())
    {
        auto [d, i, u] = q.top();
        q.pop();
        // Dijkstra：已经加入 S 集合代表最短路已确定就跳过
        if (vis[u][i])
            continue;
        vis[u][i] = 1;
        // auto [v, w] 结构化绑定 C++ 17语法，信奥赛目前禁用
        for (auto [v, w] : e[u])
        {
            if (a[v] == 1 &amp;&amp; abs(i + 1 - 10) &lt;= k) // 买了可乐
            {
                int j = i + 1;
                if (dis[v][j] &gt; dis[u][i] + w)
                {
                    dis[v][j] = dis[u][i] + w;
                    q.push({dis[v][j], j, v});
                }
            }
            if (a[v] == -1 &amp;&amp; abs(i - 1 - 10) &lt;= k)
            {
                int j = i - 1;
                if (dis[v][j] &gt; dis[u][i] + w)
                {
                    dis[v][j] = dis[u][i] + w;
                    q.push({dis[v][j], j, v});
                }
            }
        }
    }
    // min_element 返回最小值的迭代器（指针），通过 * 解引用获取具体的值。
    int ans = *min_element(dis[t].begin(), dis[t].end());
    if (ans == 1e9)
        ans = -1;
    cout &lt;&lt; ans &lt;&lt; "\n";
}
int main()
{
    int t;
    cin &gt;&gt; t;
    while (t--)
        solve();
    return 0;
}
