满分代码 <pre><code>#include &lt;bits/stdc++.h&gt;
using namespace std;

int main()
{
    ios::sync_with_stdio(false);
    cin.tie(0);
    int N, M, C;
    cin &gt;&gt; N &gt;&gt; M &gt;&gt; C;
    vector&lt;vector&lt;pair&lt;int, int&gt;&gt;&gt; e(N + 1);
    // 读取 M 条特殊边
    for (int i = 0; i &lt; M; i++)
    {
        int u, v, w;
        cin &gt;&gt; u &gt;&gt; v &gt;&gt; w;
        e[u].push_back({v, w});
    }
    // 普通异或边：逐位翻转。注意 0 号点也要考虑进来
    for (int i = 0; i &lt;= N; i++)
    {
        for (int k = 0; k &lt;= 16; k++) // n 最多 1e5，至多考虑到 2 的 16 次方即可
        {
            int j = i ^ (1 &lt;&lt; k);
            if (j &lt;= N)
            {
                e[i].push_back({j, (1 &lt;&lt; k) * C});
            }
        }
    }
    int A, B;
    cin &gt;&gt; A &gt;&gt; B;
    // Dijkstra
    vector&lt;long long&gt; dist(N + 1, 1e18);
    vector&lt;bool&gt; vis(N + 1, false);
